     this . topLeft   =   topLeft ; 
     this . width   =   width ; 
     this . height   =   height ; 
   }  }  class   Square   extends   Rectangle   { 
   Square ( Point   topLeft ,   int   width )   { 
       super ( topLeft ,   width ,   width ); 
